INSERT INTO tb_libro (id, ISBN, autor, descripcion, fecha_publicacion, fecha_registro, foto_libro, is_activo, stock, titulo, id_categoria, id_local) VALUES ('1', '978-6-2402-4778-8', 'Brandon Sanderson', 'Primer libro de una decalogía del género fantasía en un universo totalmente imaginario', '2012-04-11', now(), 'el-camino-de-los-reyes.png', 1, 100, 'El camino de los reyes', 1, 3);
INSERT INTO tb_libro (id, ISBN, autor, descripcion, fecha_publicacion, fecha_registro, foto_libro, is_activo, stock, titulo, id_categoria, id_local) VALUES ('2', '978-8-8907-6222-2', 'Brandon Sanderson', 'Palabras radiantes es la continuación de El camino de los reyes, la aclamada primera parte de la serie en diez volúmenes "The Stormlight Archive". En ella retrocedemos seis años en el tiempo, cuando un asesino, entre cuyos primeros objetivos se halla Dalinar, mata al rey alezi. Kaladin está al mando de los guardaespaldas reales, un puesto controvertido por su baja condición, y debe proteger al rey y a Dalinar, y al mismo tiempo dominar, en secreto, los nuevos y extraordinarios poderes vinculados a sus honorspren. Shallan tiene la misión de impedir el fin de las Desolaciones. Las Llanuras Quebradas poseen la respuesta; en ellas los parshendi están convencidos, gracias a su líder, de arriesgarlo todo en una apuesta desesperada...', '2015-01-11', now(), 'palabras-radiantes.jpg', 1, 10, 'Palabras Radiantes', 1, 3);
INSERT INTO tb_libro (id, ISBN, autor, descripcion, fecha_publicacion, fecha_registro, foto_libro, is_activo, stock, titulo, id_categoria, id_local) VALUES ('3', '978-5-9191-3897-6', 'Brandon Sanderson', 'La humanidad se enfrenta a una nueva Desolación con el regreso de los Portadores del Vacío, un enemigo tan grande en número como en sed de venganza. La victoria fugaz de los ejércitos alezi de Dalinar Kholin ha tenido consecuencias: el enemigo parshendi ha convocado la violenta tormenta eterna, que arrasa el mundo y hace que los hasta ahora pacíficos parshmenios descubran con horror que llevan un milenio esclavizados por los humanos. Al mismo tiempo, en una desesperada huida para alertar a su familia de la amenaza, Kaladin se pregunta si la repentina ira de los parshmenios está justificada. Entretanto, en la torre de la ciudad de Urithiru, a salvo de la tormenta, Shallan Davar investiga las maravillas de la antigua fortaleza de los Caballeros Radiantes y desentierra oscuros secretos que acechan en las profundidades. Dalinar descubre entonces que su sagrada misión de unificar su tierra natal de Alezkar era corta de miras. A menos que todas las naciones sean capaces de unirse y dejar de lado el pasado sangriento de Dalinar, ni siquiera la restauración de los Caballeros Radiantes conseguirá impedir el fin de la civilización.', '2018-01-11', now(), 'juramentada.jpg', 1, 100, 'Juramentada', 1, 3);
INSERT INTO tb_libro (id, ISBN, autor, descripcion, fecha_publicacion, fecha_registro, foto_libro, is_activo, stock, titulo, id_categoria, id_local) VALUES ('4', '978-1-4061-7949-1', 'Brandon Sanderson', 'Hace tres años, Lift le pidió a una diosa que no la dejara envejecer, un deseo que ella creía que le fue concedido. Ahora, en Danzante del Filo, la recién nacida Caballero Radiante encuentra que el tiempo no se detiene para nadie. A pesar de que el joven emperador Azish le concedió refugio de un verdugo al que sólo conoce como Oscuridad, la vida en la corte está sofocando al espíritu libre que es Lift, que no puede evitar dirigirse a Yeddaw cuando escucha que la inexorable Oscuridad está cazando gente como ella con poderes que empiezan a mostrarse.Los oprimidos en Yeddaw no tienen campeón, y Lift sabe que debe aprovechar esta increíble responsabilidad.', '2016-01-11', now(), 'danzante-del-filo.jpg', 1, 15, 'Danzante Del Filo', 1, 3);
INSERT INTO tb_libro (id, ISBN, autor, descripcion, fecha_publicacion, fecha_registro, foto_libro, is_activo, stock, titulo, id_categoria, id_local) VALUES ('5', '978-6-3334-0599-6', 'Brandon Sanderson', 'Hace años, el rey de Idris firmó un tratado con el reino de Hallandren. El rey Dedelin enviaría a su hija mayor, Vivenna, para casarse con Susebron, el rey-dios de Hallandren. Vivenna ha sido entrenada durante toda su vida para ser una novia adecuada para Susebron y así cumplir con su deber y ayudar a forjar una paz estable entre Hallandren e Idris. Ese era el plan hasta que el rey de Idris envía a su hija Siri, desobediente e independiente, en lugar de Vivenna. Siri intenta encontrar su lugar en la corte de Susebron, pero mientras lo intenta descubre la verdad oculta sobre el rey-dios. En Idris, su hermana Vivenna está preocupada y teme que Siri no esté preparada para esa nueva vida, por lo que decide viajar a Hallandren. Allí se reúne con la gente de Idris que trabaja en la capital, T’Telir, y comienza una nueva vida de espionaje y sabotaje. El plan de Vivenna es rescatar a Siri, aunque tal vez esta ni necesite ni desee ser salvada.', '2016-01-11', now(), 'el-aliento-de-los-dioses.jpg', 1, 100, 'El Aliento De Los Dioses', 1, 3);
INSERT INTO tb_libro (id, ISBN, autor, descripcion, fecha_publicacion, fecha_registro, foto_libro, is_activo, stock, titulo, id_categoria, id_local) VALUES ('6', '978-0-0944-7525-0', 'Fiódor Dostoyevski', 'Un joven soñador imagina constantemente su vejez solitaria. Una noche por las calles de San Petersburgo se encuentra con una joven, Nástenka. Él nunca había hablado con mujeres y mucho menos se había enamorado, pero hay algo de ella que le hechiza...', '2014-04-11', now(), 'las-noches-blancas.jpg', 1, 100, 'Las noches blancas', 1, 3);
INSERT INTO tb_libro (id, ISBN, autor, descripcion, fecha_publicacion, fecha_registro, foto_libro, is_activo, stock, titulo, id_categoria, id_local) VALUES ('7', '978-8-7382-4774-4', 'Fiódor Dostoyevski', 'Un joven soñador imagina constantemente su vejez solitaria. Una noche por las calles de San Petersburgo se encuentra con una joven, Nástenka. Él nunca había hablado con mujeres y mucho menos se había enamorado, pero hay algo de ella que le hechiza...', '2014-04-11', now(), 'las-noches-blancas.jpg', 1, 8, 'Las noches blancas', 1, 2);